import React, {useRef, useEffect} from 'react';

const ComponentPhoneInput = (props) => {

    let input = useRef();

    useEffect(() => {
        input.current.onfocus = () => {
            if (input.current.value.indexOf("+") === -1) {
                input.current.value = input
                    .current
                    .value
                    .indexOf("+380") == -1
                    ? "+380" + input.current.value
                    : input.current.value;
            }
        }
        input.current.onblur = () => {
            if (input.current.value.length == 4) 
                input.current.value = input.current.value.indexOf("+380") == 0
                    ? ""
                    : input.current.value;
            }
        })

    function onInput(e) {

        if (e.target.value !== "" && /(\+)|(\()|(\))|(\s)/.test(e.target.value[e.target.value.length-1])==null) {

            if (e.target.value.length > 18) {
                e.target.value = e
                    .target
                    .value
                    .substring(0, 18);
            } else {
                let tmp = e
                    .target
                    .value
                    .replace(/[^\d]/g, '');
                switch (tmp.length) {
                    case tmp.length < 3 && tmp.length >= 3:
                        break;
                    case tmp.length <= 6 && tmp.length > 3:
                        break;
                        case tmp.length <= 8 && tmp.length > 3:
                            break;

                }
            }
        }

    }

    function blurHandler(e) {
        let errorSpan = document.querySelector(".input[name='" + props.name + "'] ~ .input_error");;
        let helperSpan = document.querySelector(".input[name='" + props.name + "'] ~ .input_helper");
        if (e.target.value.match(/^[\+]{0,1}380([0-9]{9})$/) == null) {
            helperSpan
                .classList
                .add("hide")
            errorSpan
                .classList
                .remove("hide")
            errorSpan.innerText = "Invalid number";
        } else {
            input
                .current
                .classList
                .remove("error");
            helperSpan
                .classList
                .remove("hide")
            errorSpan
                .classList
                .add("hide")
        }
    }

    if (props.reqired) {
        return (
            <label className='input_label'>
                <input
                    ref={input}
                    type="tel"
                    className={"input"}
                    required
                    onInput={onInput}
                    onPaste={onInput}
                    onBlur={blurHandler}
                    placeholder={props.placeholder}
                    name={props.name}/>
                <span className={"input_helper"}>{"+38 (XXX) XXX - XX - XX"}</span>
                <span className={"input_error hide"}></span>
            </label>
        );
    } else {
        return (
            <label className='input_label'><input
                ref={input}
                type={"text"}
                className={"input"}
                onInput={onInput}
                placeholder={props.placeholder}
                name={props.name}/>
                <span className={"input_helper"}>{"+38 (XXX) XXX - XX - XX"}</span>
                <span className={"input_error hide"}></span>
            </label>
        );
    }
}

export default ComponentPhoneInput;
